using System.Collections.ObjectModel;
using Cnbv.Sait.ActiveDirectory;
using Cnbv.Sait.Utilities.Extensions;
public class TestClass
{
  public void Directory()
  {
    //se conecta al dominio sobre el cual esta conectada la maquina
    DirectoryDomain dominio = new DirectoryDomain();
    //se obtiene a la unidad organizacional
    IDirectoryOrganizationalUnit ou = dominio.SearchOrganizationalUnit("ou");
    if(ou.IsNotNull())
    {
      //buscar una computadora
      IDirectoryComputer computadora = ou.SearchComputer("computadora");
      //buscamos todas las computadoras
      ObservableCollection<IDirectoryComputer> computadoras = ou.SearchComputers();
      //buscar un grupo
      IDirectoryGroup grupo = ou.SearchGroup("grupo");
      //buscamos todas los grupos
      ObservableCollection<IDirectoryGroup> grupos = ou.SearchGroups();
      //buscar una unidad organizacional
      IDirectoryOrganizationalUnit unidad = ou.SearchOrganizationalUnit("unidad");
      //buscamos todas las unidades organizacionales
      ObservableCollection<IDirectoryOrganizationalUnit> unidades =
        ou.SearchOrganizationalUnits();
      //buscar una impresora
      IDirectoryPrinter impresora = ou.SearchPrinter("impresora");
      //buscamos todas las impresoras
      ObservableCollection<IDirectoryPrinter> impresoras = ou.SearchPrinters();
      //buscar un usuario por id 
      IDirectoryUser usuario1 = ou.SearchUserById("id");
      //buscar un usuario por nombre 
      IDirectoryUser usuario2 = ou.SearchUserByName("nombre");
      //buscamos todas los usuarios
      ObservableCollection<IDirectoryUser> usuarios = ou.SearchUsers();
    }
  }
}

using System.Collections.ObjectModel;
using Cnbv.Sait.ActiveDirectory;
using Cnbv.Sait.Utilities.Extensions;
public class TestClass
{
  public void Directory()
  {
    //se conecta al dominio sobre el cual esta conectada la maquina
    DirectoryDomain dominio = new DirectoryDomain();
    //se obtiene el grupo
    IDirectoryGroup grupo = dominio.SearchGroup("grupo");
    if(grupo.IsNotNull())
    {
      //agrega un usuario al grupo
      grupo.AddUser("id");
      //quita un usuario del grupo
      grupo.RemoveUser("id");
      //actualiza la descripción del grupo
      grupo.EditDescription("descripción");
      //validar si un usuario es miembro del grupo
      if(grupo.IsMember("id"))
      {
        //el usuario es miembro del grupo
      }
      //buscar un grupo que sea miembro del grupo actual
      IDirectoryGroup grupo = grupo.SearchGroup("grupoHijo");
      //buscamos todas los grupos que son miembros del grupo actual
      ObservableCollection<IDirectoryGroup> grupos = grupo.SearchGroups();
      //buscar un usuario que sea miembro del grupo por id 
      IDirectoryUser usuario1 = grupo.SearchUserById("id");
      //buscar un usuario que sea miembro del grupo por nombre 
      IDirectoryUser usuario2 = grupo.SearchUserByName("nombre");
      //buscamos todas los usuarios que son miembros del grupo
      ObservableCollection<IDirectoryUser> usuarios = grupo.SearchUsers();
    }
  }
}

using System.Collections.ObjectModel;
using Cnbv.Sait.ActiveDirectory;
using Cnbv.Sait.Utilities.Extensions;
public class TestClass
{
  public void Directory()
  {
    //se conecta al dominio sobre el cual esta conectada la maquina
    DirectoryDomain dominio = new DirectoryDomain();
    //cambiar de dominio
    dominio.ChangeDomain("cnbyv","DC=CNBYV,DC=GOB");
    //buscar una computadora
    IDirectoryComputer computadora = dominio.SearchComputer("computadora");
    //buscamos todas las computadoras
    ObservableCollection<IDirectoryComputer> computadoras = dominio.SearchComputers();
    //buscar un contenedor
    IDirectoryContainer contenedor = dominio.SearchContainer("contenedor");
    //buscamos todas los contenedores
    ObservableCollection<IDirectoryContainer> contenedores = dominio.SearchContainers();
    //buscar un grupo
    IDirectoryGroup grupo = dominio.SearchGroup("grupo");
    //buscamos todas los grupos
    ObservableCollection<IDirectoryGroup> grupos = dominio.SearchGroups();
    //buscar una unidad organizacional
    IDirectoryOrganizationalUnit unidad = dominio.SearchOrganizationalUnit("unidad");
    //buscamos todas las unidades organizacionales
    ObservableCollection<IDirectoryOrganizationalUnit> unidades = 
      dominio.SearchOrganizationalUnits();
    //buscar una impresora
    IDirectoryPrinter impresora = dominio.SearchPrinter("impresora");
    //buscamos todas las impresoras
    ObservableCollection<IDirectoryPrinter> impresoras = dominio.SearchPrinters();
    //buscar un usuario por id 
    IDirectoryUser usuario1 = dominio.SearchUserById("id");
    //buscar un usuario por nombre 
    IDirectoryUser usuario2 = dominio.SearchUserByName("nombre");
    //buscamos todas los usuarios
    ObservableCollection<IDirectoryUser> usuarios = dominio.SearchUsers();
  }
}

using System.Collections.ObjectModel;
using Cnbv.Sait.ActiveDirectory;
using Cnbv.Sait.Utilities.Extensions;
public class TestClass
{
  public void Directory()
  {
    //se conecta al dominio sobre el cual esta conectada la maquina
    DirectoryDomain dominio = new DirectoryDomain();
    //se obtiene a la computadora
    IDirectoryComputer computadora = dominio.SearchComputer("computadora");
    if(computadora.IsNotNull())
    {
      //buscar una impresora
      IDirectoryPrinter impresora = computadora.SearchPrinter("impresora");
      //buscamos todas las impresoras
      ObservableCollection<IDirectoryPrinter> impresoras = computadora.SearchPrinters();
    }
  }
}

using System.Collections.ObjectModel;
using Cnbv.Sait.ActiveDirectory;
using Cnbv.Sait.Utilities.Extensions;
public class TestClass
{
  public void Directory()
  {
    //se conecta al dominio sobre el cual esta conectada la maquina
    DirectoryDomain dominio = new DirectoryDomain();
    //se obtiene al usuario
    IDirectoryUser usuario = dominio.SearchUserById("id");
    if(usuario.IsNotNull())
    {
      //validar si el usuario es miembro de otro grupo
      if(usuario.IsMemberOf("grupo"))
      {
        //el grupo es miembro del grupo
      }
      //buscar los grupos de los cuales se es miembro
      ObservableCollection<IDirectoryGroup> grupos = 
        usuario.MemberOf(GroupTypes.GlobalDistribution);
    }
  }
}

