using System.Collections.ObjectModel;
using Cnbv.Sait.ActiveDirectory;
using Cnbv.Sait.Utilities.Extensions;
public class TestClass
{
  public void Directory()
  {
    //se conecta al dominio sobre el cual esta conectada la maquina
    DirectoryDomain dominio = new DirectoryDomain();
    //se obtiene al contenedor
    IDirectoryContainer contenedor = dominio.SearchContainer("contenedor");
    if(contenedor.IsNotNull())
    {
      //buscar una computadora
      IDirectoryComputer computadora = contenedor.SearchComputer("computadora");
      //buscamos todas las computadoras
      ObservableCollection<IDirectoryComputer> computadoras = contenedor.SearchComputers();
      //buscar un grupo
      IDirectoryGroup grupo = contenedor.SearchGroup("grupo");
      //buscamos todas los grupos
      ObservableCollection<IDirectoryGroup> grupos = contenedor.SearchGroups();
      //buscar una impresora
      IDirectoryPrinter impresora = contenedor.SearchPrinter("impresora");
      //buscamos todas las impresoras
      ObservableCollection<IDirectoryPrinter> impresoras = contenedor.SearchPrinters();
      //buscar un usuario por id
      IDirectoryUser usuario1 = contenedor.SearchUserById("id");
      //buscar un usuario por nombre 
      IDirectoryUser usuario2 = contenedor.SearchUserByName("nombre");
      //buscamos todas los usuarios
      ObservableCollection<IDirectoryUser> usuarios = contenedor.SearchUsers();
    }
  }
}
